// Prepare data for Dashboard
var chartMessages = [];
var gaugeData = 0;

if (Array.isArray(msg.payload)) {
    // Calculate average AQI
    var totalAqi = 0;

    msg.payload.forEach(function(record) {
        totalAqi += record.aqi;
        // Create a message for each station
        chartMessages.push({
            payload: record.aqi,
            topic: record.station
        });
    });

    gaugeData = Math.round(totalAqi / msg.payload.length);
}

// Return: first output is bar chart data (multiple messages), second output is gauge data
return [
    chartMessages,
    {payload: gaugeData, topic: "Average AQI"}
];

