// Simulate traffic data (should be replaced with real API calls)
var timestamp = new Date();
var hour = timestamp.getHours();

// Simulate traffic density based on time (peak hours are more congested)
var isPeakHour = (hour >= 7 && hour <= 9) || (hour >= 17 && hour <= 19);

var locations = [
    {name: "Central Connaught Rd", lat: 22.2819, lon: 114.1577},
    {name: "Causeway Bay Hennessy Rd", lat: 22.2793, lon: 114.1847},
    {name: "Mong Kok Nathan Rd", lat: 22.3193, lon: 114.1694},
    {name: "Tsim Sha Tsui Canton Rd", lat: 22.2976, lon: 114.1722},
    {name: "Kwun Tong Rd", lat: 22.3122, lon: 114.2259}
];

var densityLevels = ["Smooth", "Slow", "Congested"];

var records = locations.map(function(location) {
    var density;
    if (isPeakHour) {
        density = densityLevels[Math.floor(Math.random() * 2) + 1]; // Slow or Congested
    } else {
        density = densityLevels[Math.floor(Math.random() * 2)]; // Smooth or Slow
    }

    var vehicleCount = 0;
    var avgSpeed = 0;

    if (density === "Smooth") {
        vehicleCount = Math.floor(Math.random() * 40) + 10;  // 10-50 vehicles
        avgSpeed = Math.floor(Math.random() * 20) + 50;     // 50-70 km/h
    } else if (density === "Slow") {
        vehicleCount = Math.floor(Math.random() * 50) + 60;  // 60-110 vehicles
        avgSpeed = Math.floor(Math.random() * 20) + 30;     // 30-50 km/h
    } else {
        vehicleCount = Math.floor(Math.random() * 50) + 110; // 110-160 vehicles
        avgSpeed = Math.floor(Math.random() * 20) + 10;     // 10-30 km/h
    }

    return {
        location: location.name,
        traffic_density: density,
        vehicle_count: vehicleCount,
        average_speed: avgSpeed,
        timestamp: timestamp,
        coordinates: {
            type: "Point",
            coordinates: [location.lon, location.lat]
        }
    };
});

msg.payload = records;
msg.collection = "traffic_flow";

return msg;

// Prepare multiple traffic visualization data
var densityCounts = {
    "Smooth": 0,
    "Slow": 0,
    "Congested": 0
};

var speedMessages = [];
var volumeMessages = [];

if (Array.isArray(msg.payload)) {
    msg.payload.forEach(function(record) {
        // Count traffic density
        if (densityCounts.hasOwnProperty(record.traffic_density)) {
            densityCounts[record.traffic_density]++;
        }

        // Prepare speed chart data
        speedMessages.push({
            topic: record.location,
            payload: record.average_speed
        });

        // Prepare volume chart data
        volumeMessages.push({
            topic: record.location,
            payload: record.vehicle_count
        });
    });
}

// Prepare density pie chart data
var densityMessages = [];
for (var density in densityCounts) {
    densityMessages.push({
        topic: density,
        payload: densityCounts[density]
    });
}

// Return: 0-density pie, 1-speed bar, 2-volume bar
return [densityMessages, speedMessages, volumeMessages];
